CREATE TABLE "academic"."class_representative" ("id" uuid NOT NULL, "user_id" uuid NOT NULL, "section_id" uuid NOT NULL, "batch_id" uuid NOT NULL, "appointed_at" date, "is_active" boolean NOT NULL DEFAULT true, PRIMARY KEY ("id") , FOREIGN KEY ("user_id") REFERENCES "user"."account"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("section_id") REFERENCES "academic"."section"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("batch_id") REFERENCES "academic"."batch"("id") ON UPDATE restrict ON DELETE cascade, UNIQUE ("user_id", "section_id"));
