CREATE TABLE "event"."event_cancellation" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "event_id" uuid NOT NULL, "cancelled_by" uuid NOT NULL, "reason" text NOT NULL, "cancelled_at" timestamptz NOT NULL DEFAULT now(), "rescheduled_event_id" uuid, "notification_sent" boolean NOT NULL DEFAULT false, "notification_sent_at" timestamptz, PRIMARY KEY ("id") );COMMENT ON TABLE "event"."event_cancellation" IS E'Cancellation records with rescheduling links.';
