CREATE TABLE "event"."event_attachment" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "event_id" uuid NOT NULL, "attachment_type" text NOT NULL, "title" text NOT NULL, "url" text NOT NULL, "file_size" integer, "mime_type" text, "description" text, "uploaded_by" uuid, "created_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") );COMMENT ON TABLE "event"."event_attachment" IS E'Documents and resources attached to events.';
